import React, { useState } from "react";
const ItemElements = ({ setShowAddItem }) => {
  const LINE_ITEM = {
    item: "",
    quantity: "",
    price: "",
    total: "",
  };
  const tableHeaders = ["Item", "Quantity", "Price", "Total"];

  const [data, setData] = useState([() => LINE_ITEM]);

  const handleAdd = (e) => {
    e.preventDefault();
    setData((prevData) => {
      [...prevData, ...LINE_ITEM];
    });
  };


  const handleData = (event, listItemIndex, keyIndex ) => {
    let data = {...data}
    data[listItemIndex][keyIndex] = event.target.value
    // update the state
    setData(() => data)
  };
  return (
    <div>
      <h3>Add Item</h3>
      <button
        onClick={(e) => {
          e.preventDefault();
          setShowAddItem(false);
        }}
      >
        x
      </button>
      <table>
        <thead>
          <tr>
            {tableHeaders.map((header, index) => {
              return <th key={index}>{header}</th>;
            })}
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => {
            return (
              <tr>
                {Object.keys(item).map((key, keyIndex) => {
                  return <td key={keyIndex}>
                    <input
                        type={key === 'item' ? 'text' : 'number'}
                        value = {item[key]}
                        name = {key}
                        onChange = {(e) => handleDataChange(e, index , key)}
                    />
                  </td>;
                })}
                <td></td>
              </tr>
            );
          })}

          <tr>
            <td>
              <input
                type="text"
                name="item"
                value={data.item}
                onChange={handleData}
              />
            </td>
            <td>
              <input
                type="text"
                name="quantity"
                value={data.quantity}
                onChange={handleData}
              />
            </td>
            <td>
              <input
                type="text"
                name="total"
                value={data.total}
                onChange={handleData}
              />
            </td>
          </tr>
          <tr>
            <td>
              <button onClick={handleAdd}>Add</button>
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
};

export default ItemElements;
