import React, { useState } from "react"
const ItemElements = ({setShowAddItem}) =>  {
    const [data, setData]= useState({item:"", quantity:0, total:0});

    let [dataEntered, setDataEntered] = useState([]);

    const handleAdd = (e) => {
        e.preventDefault();
        console.log(data);
        // let temp=[...data];
        // let count = temp[temp.length-1]
        // console.log(count);
        // temp.push(count+1);
        // setData(temp)
    }

    const handleData =(e)=>{
        console.log(e.target.name);
        console.log(e.target.value);
        let temp = {...data};
        if(e.target.name === "item") {
            temp.item = e.target.value;
            setData(temp)
        }
        if(e.target.name === "quantity") {
            temp.quantity = e.target.value;
            setData(temp)
        }
        if(e.target.name === "total") {
            temp.total = e.target.value;
            setData(temp)
        }
    }
    return ( 
        <div><h3>Add Item</h3>
        <button onClick={(e)=>{e.preventDefault(); setShowAddItem(false)}}>x</button>
        <table><thead>
            <tr>
                <th>item</th>
                <th>quantity</th>
                <th>total</th>
                </tr>
                </thead>
                <tbody>
                    {dataEntered?.map(list=> {
                        return <tr>
                            <td><input type="text" name="item" value={list.item} /></td>
                            <td><input type="text" name="quantity" value={list.quantity} /></td>
                            <td><input type="text" name="total" value={list.total} /></td>
                        </tr>
                    })}

                        {/* {data?.map((val,index) => {return <tr key={index}> */}
                        <tr>
                            <td><input type="text" name="item" value={data.item} onChange={handleData} /></td>
                            <td><input type="text" name="quantity" value={data.quantity} onChange={handleData} /></td>
                            <td><input type="text" name="total" value={data.total} onChange={handleData} /></td>
                            </tr>
                            {/* })} */}
                    <tr>
                        <td><button onClick={handleAdd}>Add</button></td>
                    </tr>
                </tbody>
                </table>
                </div>
    )
}

export default ItemElements;
