import React, { useState } from "react"
const ItemElements = ({setShowAddItem}) =>  {
    const [data, setData]= useState([1]);

    let [dataEntered, etDataEntered] = useState([{item:"", quantity:0, total:0}]);

    const handleAdd = (e) => {
        e.preventDefault();
        let temp=[...data];
        let count = temp[temp.length-1]
        console.log(count);
        temp.push(count+1);
        setData(temp)
    }
    return ( 
        <div><h3>Add Item</h3>
        <button onClick={(e)=>{e.preventDefault(); setShowAddItem(false)}}>x</button>
        <table><thead>
            <tr>
                <th>one</th>
                <th>two</th>
                <th>three</th>
                <th>four</th></tr></thead>
                <tbody>

                        {data?.map(val => {return <tr><td>{val}</td>
                        <td>{val}</td>
                        <td>{val}</td>
                        <td>{val}</td>
                        </tr>})}
                    {/* <td>one</td>
                <td>two</td>
                <td>three</td>
                <td>four</td> */}
                    <tr>
                        <td><button onClick={handleAdd}>Add</button></td>
                    </tr>
                </tbody>
                </table>
                </div>
    )
}

export default ItemElements;
