import React, { useEffect, useState } from 'react';
import { Form } from 'react-bootstrap';
import DatePicker from "react-datepicker";
import ItemElements from './ItemElements';

const BasicElements = () => {
  const [startDate, setStartDate] = useState(new Date())
  const [showAddItem, setShowAddItem] = useState(false);
  const LINE_ITEM = {
    item: "",
    quantity: "",
    price: "",
    total: "",
  };
  const tableHeaders = ["Item", "Quantity", "Price", "Total"];

  const [data, setData] = useState([LINE_ITEM]);

  const handleAdd = (e) => {
    e.preventDefault()
    let temp = [...data]
    temp.push(LINE_ITEM)
    setData(temp)
  };


  const handleData = (event, listItemIndex, keyIndex ) => {
    let temp = {...data}
    temp[listItemIndex][keyIndex] = event.target.value
    // update the state
    setData(() => temp)
  };

  const handleChange = date => {
    setStartDate(date);
  };

  useEffect(() => {
    console.log(data)
  },[data])
    return (
      <div>
        <div className="page-header">
          <h3 className="page-title"> Create Invoice </h3>
          <nav aria-label="breadcrumb">
            <ol className="breadcrumb">
              <li className="breadcrumb-item"><a href="!#" onClick={event => event.preventDefault()}>Forms</a></li>
              <li className="breadcrumb-item active" aria-current="page">Form elements</li>
            </ol>
          </nav>
        </div>
          <div className="col-12 grid-margin stretch-card">
            <div className="card">
              <div className="card-body">
                <h4 className="card-title">Create Bill</h4>
                <p className="card-description"> Basic form elements </p>
                <form className="forms-sample">
                  <Form.Group>
                    <label htmlFor="exampleInputName1">Customer Name</label>
                    <Form.Control type="text" className="form-control" id="exampleInputName1" placeholder="name" />
                  </Form.Group>
                  <Form.Group>
                    <label htmlFor="exampleInputEmail3">Email address</label>
                    <Form.Control type="email" className="form-control" id="exampleInputEmail3" placeholder="Email" />
                  </Form.Group>
                  <Form.Group>
                    <label htmlFor="exampleInputPassword4">Mobile No</label>
                    <Form.Control type="password" className="form-control" id="exampleInputPassword4" placeholder="number" />
                    </Form.Group>
                  <Form.Group>
                    <label htmlFor="exampleInputPassword4">Next Service Date</label>
                    <div></div>
                    <DatePicker className="form-control w-100"
                          selected={startDate}
                          onChange={handleChange}
                        />
                    </Form.Group>
                  <div>
                    <button onClick={handleAdd}>Add Item</button>
                  </div>
                  {data?.map((item, index) => {
                    return(<p>test</p>)
                  })}
                  <button type="submit" className="btn btn-gradient-primary mr-2">Submit</button>
                  <button className="btn btn-light">Cancel</button>
                </form>
              </div>
            </div>
          </div>

        </div>
      // </div>
    )
}

export default BasicElements
