import React, { useState } from "react"
const ItemElements = ({setShowAddItem}) =>  {
    const [data, setData]= useState([{item:"", quantity:0, total:0}]);

    let [dataEntered, setDataEntered] = useState([]);

    const handleAdd = (e) => {
        e.preventDefault();
        let temp=[...data];
        let count = temp[temp.length-1]
        console.log(count);
        temp.push(count+1);
        setData(temp)
    }

    const handleData =(e)=>{
        console.log(e.target.name);
        console.log(e.target.value);
        if(e.target.name === "item") {
            setData({item: e.target.value})
        }
    }
    return ( 
        <div><h3>Add Item</h3>
        <button onClick={(e)=>{e.preventDefault(); setShowAddItem(false)}}>x</button>
        <table><thead>
            <tr>
                <th>item</th>
                <th>quantity</th>
                <th>total</th>
                </tr>
                </thead>
                <tbody>

                        {data?.map(val => {return <tr>
                            <td><input tyep="text" name="item" value={data.item} onChange={handleData} /></td>
                            <td><input tyep="text" name="quantity" value={data.quantity} onChange={handleData} /></td>
                            <td><input tyep="text" name="total" value={data.total} onChange={handleData} /></td>
                            </tr>})}
                    <tr>
                        <td><button onClick={handleAdd}>Add</button></td>
                    </tr>
                </tbody>
                </table>
                </div>
    )
}

export default ItemElements;
